import React, { useState } from 'react';
import { User, Phone, Mail, Search, Plus, Edit2, Trash2, Users, BookOpen, Settings, LogOut } from 'lucide-react';
//import ContactForm from './ContactForm';
import CategoryForm from './CategoryForm';

const Dashboard = ({ currentUser, onLogout }) => {
  const [contacts, setContacts] = useState([
    {
      contact_id: 1,
      name: 'Alice Johnson',
      email: 'alice@example.com',
      phone: '+1 (555) 123-4567',
      category_id: 1,
      user_id: currentUser.user_id
    },
    {
      contact_id: 2,
      name: 'Bob Smith',
      email: 'bob@company.com',
      phone: '+1 (555) 234-5678',
      category_id: 3,
      user_id: currentUser.user_id
    },
    {
      contact_id: 3,
      name: 'Carol Wilson',
      email: 'carol@example.com',
      phone: '+1 (555) 345-6789',
      category_id: 2,
      user_id: currentUser.user_id
    }
  ]);

  const [categories, setCategories] = useState([
    { category_id: 1, name: 'Family' },
    { category_id: 2, name: 'Friends' },
    { category_id: 3, name: 'Work' },
    { category_id: 4, name: 'Business' }
  ]);

  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [showAddContact, setShowAddContact] = useState(false);
  const [editingContact, setEditingContact] = useState(null);
  const [showAddCategory, setShowAddCategory] = useState(false);
  const [activeTab, setActiveTab] = useState('contacts');

  const styles = {
    dashboard: {
      display: 'flex',
      minHeight: '100vh',
    },
    sidebar: {
      width: '250px',
      backgroundColor: 'rgba(255,255,255,0.95)',
      backdropFilter: 'blur(10px)',
      padding: '2rem 1rem',
      borderRadius: '0 20px 20px 0',
      boxShadow: '5px 0 20px rgba(0,0,0,0.1)',
    },
    sidebarTitle: {
      fontSize: '1.5rem',
      fontWeight: 'bold',
      marginBottom: '2rem',
      color: '#333',
      textAlign: 'center',
    },
    navItem: {
      display: 'flex',
      alignItems: 'center',
      padding: '0.75rem 1rem',
      margin: '0.5rem 0',
      borderRadius: '10px',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      color: '#555',
    },
    navItemActive: {
      backgroundColor: '#667eea',
      color: 'white',
    },
    navIcon: {
      marginRight: '0.5rem',
    },
    mainContent: {
      flex: 1,
      padding: '2rem',
    },
    header: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      marginBottom: '2rem',
      color: 'white',
    },
    headerTitle: {
      fontSize: '2rem',
      fontWeight: 'bold',
    },
    userInfo: {
      display: 'flex',
      alignItems: 'center',
      gap: '1rem',
    },
    avatar: {
      width: '40px',
      height: '40px',
      backgroundColor: '#ff6b6b',
      borderRadius: '50%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      color: 'white',
      fontWeight: 'bold',
    },
    controls: {
      display: 'flex',
      gap: '1rem',
      marginBottom: '2rem',
      flexWrap: 'wrap',
    },
    searchBox: {
      flex: 1,
      minWidth: '200px',
      position: 'relative',
    },
    searchInput: {
      width: '100%',
      padding: '0.75rem 1rem 0.75rem 3rem',
      border: '2px solid rgba(255,255,255,0.3)',
      borderRadius: '25px',
      fontSize: '1rem',
      backgroundColor: 'rgba(255,255,255,0.9)',
      outline: 'none',
    },
    searchIcon: {
      position: 'absolute',
      left: '1rem',
      top: '50%',
      transform: 'translateY(-50%)',
      color: '#666',
    },
    select: {
      padding: '0.75rem 1rem',
      border: '2px solid rgba(255,255,255,0.3)',
      borderRadius: '10px',
      backgroundColor: 'rgba(255,255,255,0.9)',
      fontSize: '1rem',
      outline: 'none',
    },
    addButton: {
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      padding: '0.75rem 1.5rem',
      backgroundColor: '#ff6b6b',
      color: 'white',
      border: 'none',
      borderRadius: '10px',
      cursor: 'pointer',
      fontSize: '1rem',
      transition: 'all 0.3s ease',
    },
    contactGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
      gap: '1.5rem',
    },
    contactCard: {
      backgroundColor: 'rgba(255,255,255,0.95)',
      borderRadius: '15px',
      padding: '1.5rem',
      boxShadow: '0 10px 30px rgba(0,0,0,0.1)',
      transition: 'all 0.3s ease',
      transform: 'translateY(0)',
    },
    contactHeader: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'flex-start',
      marginBottom: '1rem',
    },
    contactName: {
      fontSize: '1.25rem',
      fontWeight: 'bold',
      color: '#333',
      marginBottom: '0.5rem',
    },
    contactCategory: {
      fontSize: '0.875rem',
      color: '#667eea',
      backgroundColor: 'rgba(102, 126, 234, 0.1)',
      padding: '0.25rem 0.75rem',
      borderRadius: '20px',
      fontWeight: '500',
    },
    contactInfo: {
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      marginBottom: '0.5rem',
      color: '#666',
    },
    contactActions: {
      display: 'flex',
      gap: '0.5rem',
      marginTop: '1rem',
    },
    actionButton: {
      padding: '0.5rem',
      border: 'none',
      borderRadius: '8px',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    },
    editButton: {
      backgroundColor: '#feca57',
      color: 'white',
    },
    deleteButton: {
      backgroundColor: '#ff6b6b',
      color: 'white',
    },
    emptyState: {
      textAlign: 'center',
      color: 'rgba(255,255,255,0.8)',
      fontSize: '1.25rem',
      padding: '3rem',
    },
  };

  // Contact Management Functions
  const addContact = (contactData) => {
    const newContact = {
      contact_id: Date.now(),
      ...contactData,
      user_id: currentUser.user_id
    };
    setContacts([...contacts, newContact]);
    setShowAddContact(false);
  };

  const updateContact = (contactData) => {
    setContacts(contacts.map(contact => 
      contact.contact_id === editingContact.contact_id 
        ? { ...contact, ...contactData }
        : contact
    ));
    setEditingContact(null);
  };

  const deleteContact = (contactId) => {
    if (window.confirm('Are you sure you want to delete this contact?')) {
      setContacts(contacts.filter(contact => contact.contact_id !== contactId));
    }
  };

  const addCategory = (categoryName) => {
    const newCategory = {
      category_id: Date.now(),
      name: categoryName
    };
    setCategories([...categories, newCategory]);
    setShowAddCategory(false);
  };

  // Filter contacts based on search and category
  const filteredContacts = contacts.filter(contact => {
    const matchesSearch = contact.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         contact.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         contact.phone.includes(searchTerm);
    const matchesCategory = selectedCategory === '' || contact.category_id.toString() === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  // Get category name by ID
  const getCategoryName = (categoryId) => {
    const category = categories.find(cat => cat.category_id === categoryId);
    return category ? category.name : 'Unknown';
  };

  const sidebarItems = [
    { id: 'contacts', label: 'Contacts', icon: Users },
    { id: 'categories', label: 'Categories', icon: BookOpen },
    { id: 'settings', label: 'Settings', icon: Settings },
  ];

  return (
    <div style={styles.dashboard}>
      <div style={styles.sidebar}>
        <h2 style={styles.sidebarTitle}>Contact Book</h2>
        <nav>
          {sidebarItems.map(item => {
            const Icon = item.icon;
            return (
              <div
                key={item.id}
                style={{
                  ...styles.navItem,
                  ...(activeTab === item.id ? styles.navItemActive : {})
                }}
                onClick={() => setActiveTab(item.id)}
              >
                <Icon size={20} style={styles.navIcon} />
                {item.label}
              </div>
            );
          })}
          <div
            style={{...styles.navItem, marginTop: '2rem'}}
            onClick={onLogout}
          >
            <LogOut size={20} style={styles.navIcon} />
            Logout
          </div>
        </nav>
      </div>

      <div style={styles.mainContent}>
        <div style={styles.header}>
          <h1 style={styles.headerTitle}>
            {activeTab === 'contacts' ? 'My Contacts' : 
             activeTab === 'categories' ? 'Categories' : 'Settings'}
          </h1>
          <div style={styles.userInfo}>
            <div style={styles.avatar}>
              {currentUser?.name?.charAt(0).toUpperCase()}
            </div>
            <span>{currentUser?.name}</span>
          </div>
        </div>

        {activeTab === 'contacts' && (
          <>
            <div style={styles.controls}>
              <div style={styles.searchBox}>
                <Search size={20} style={styles.searchIcon} />
                <input
                  type="text"
                  placeholder="Search contacts..."
                  style={styles.searchInput}
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>
              <select
                style={styles.select}
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
              >
                <option value="">All Categories</option>
                {categories.map(category => (
                  <option key={category.category_id} value={category.category_id}>
                    {category.name}
                  </option>
                ))}
              </select>
              <button
                style={styles.addButton}
                onClick={() => setShowAddContact(true)}
              >
                <Plus size={20} />
                Add Contact
              </button>
            </div>

            {filteredContacts.length === 0 ? (
              <div style={styles.emptyState}>
                {contacts.length === 0 ? 
                  'No contacts yet. Add your first contact!' : 
                  'No contacts match your search criteria.'}
              </div>
            ) : (
              <div style={styles.contactGrid}>
                {filteredContacts.map(contact => (
                  <div
                    key={contact.contact_id}
                    style={styles.contactCard}
                    onMouseEnter={(e) => {
                      e.currentTarget.style.transform = 'translateY(-5px)';
                      e.currentTarget.style.boxShadow = '0 15px 40px rgba(0,0,0,0.2)';
                    }}
                    onMouseLeave={(e) => {
                      e.currentTarget.style.transform = 'translateY(0)';
                      e.currentTarget.style.boxShadow = '0 10px 30px rgba(0,0,0,0.1)';
                    }}
                  >
                    <div style={styles.contactHeader}>
                      <div>
                        <h3 style={styles.contactName}>{contact.name}</h3>
                        <span style={styles.contactCategory}>
                          {getCategoryName(contact.category_id)}
                        </span>
                      </div>
                    </div>
                    <div style={styles.contactInfo}>
                      <Mail size={16} />
                      <span>{contact.email}</span>
                    </div>
                    <div style={styles.contactInfo}>
                      <Phone size={16} />
                      <span>{contact.phone}</span>
                    </div>
                    <div style={styles.contactActions}>
                      <button
                        style={{...styles.actionButton, ...styles.editButton}}
                        onClick={() => setEditingContact(contact)}
                      >
                        <Edit2 size={16} />
                      </button>
                      <button
                        style={{...styles.actionButton, ...styles.deleteButton}}
                        onClick={() => deleteContact(contact.contact_id)}
                      >
                        <Trash2 size={16} />
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </>
        )}

        {activeTab === 'categories' && (
          <>
            <div style={styles.controls}>
              <button
                style={styles.addButton}
                onClick={() => setShowAddCategory(true)}
              >
                <Plus size={20} />
                Add Category
              </button>
            </div>
            <div style={styles.contactGrid}>
              {categories.map(category => (
                <div key={category.category_id} style={styles.contactCard}>
                  <h3 style={styles.contactName}>{category.name}</h3>
                  <p style={{ color: '#666' }}>
                    {contacts.filter(c => c.category_id === category.category_id).length} contacts
                  </p>
                </div>
              ))}
            </div>
          </>
        )}

        {activeTab === 'settings' && (
          <div style={styles.contactCard}>
            <h3 style={styles.contactName}>Account Settings</h3>
            <div style={styles.contactInfo}>
              <User size={16} />
              <span>{currentUser?.name}</span>
            </div>
            <div style={styles.contactInfo}>
              <Mail size={16} />
              <span>{currentUser?.email}</span>
            </div>
          </div>
        )}
      </div>

      {showAddContact && (
        <ContactForm
          categories={categories}
          onSave={addContact}
          onCancel={() => setShowAddContact(false)}
        />
      )}
      
      {editingContact && (
        <ContactForm
          contact={editingContact}
          categories={categories}
          onSave={updateContact}
          onCancel={() => setEditingContact(null)}
        />
      )}
      
      {showAddCategory && (
        <CategoryForm
          onSave={addCategory}
          onCancel={() => setShowAddCategory(false)}
        />
      )}
    </div>
  );
};

export default Dashboard;
