import React, { useState, useEffect } from 'react';
import { User, Phone, Mail, Heart, Tag, Plus, Search, Filter, Edit, Trash2, UserPlus, Users, Contact } from 'lucide-react';

const ContactManagementUI = () => {
  const [activeTab, setActiveTab] = useState('contacts');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [showAddModal, setShowAddModal] = useState(false);
  const [editingItem, setEditingItem] = useState(null);
  const [currentUser, setCurrentUser] = useState(null);

  // Sample data
  const [users, setUsers] = useState([
    { id: 1, name: 'John Doe', email: 'john@example.com', pwd_hash: 'hashed_password_1' },
    { id: 2, name: 'Jane Smith', email: 'jane@example.com', pwd_hash: 'hashed_password_2' }
  ]);

  const [categories, setCategories] = useState([
    { category_id: 1, name: 'Friends' },
    { category_id: 2, name: 'Family' },
    { category_id: 3, name: 'Work' },
    { category_id: 4, name: 'Business' }
  ]);

  const [contacts, setContacts] = useState([
    { contact_id: 1, name: 'Alice Johnson', phone: '+1-555-0123', email: 'alice@example.com', photo: null, is_favorite: true, category_id: 1, user_id: 1 },
    { contact_id: 2, name: 'Bob Wilson', phone: '+1-555-0456', email: 'bob@example.com', photo: null, is_favorite: false, category_id: 3, user_id: 1 },
    { contact_id: 3, name: 'Carol Brown', phone: '+1-555-0789', email: 'carol@example.com', photo: null, is_favorite: true, category_id: 2, user_id: 1 },
    { contact_id: 4, name: 'David Davis', phone: '+1-555-0321', email: 'david@example.com', photo: null, is_favorite: false, category_id: 4, user_id: 2 }
  ]);

  useEffect(() => {
    setCurrentUser(users[0]);
  }, []);

  const getCategoryName = (categoryId) => {
    const category = categories.find(cat => cat.category_id === categoryId);
    return category ? category.name : 'Unknown';
  };

  const filteredContacts = contacts.filter(contact => {
    if (currentUser && contact.user_id !== currentUser.id) return false;

    const matchesSearch = contact.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         contact.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         contact.phone.includes(searchTerm);

    const matchesCategory = selectedCategory === 'all' || 
                           selectedCategory === 'favorites' && contact.is_favorite ||
                           contact.category_id === parseInt(selectedCategory);

    return matchesSearch && matchesCategory;
  });

  const toggleFavorite = (contactId) => {
    setContacts(contacts.map(contact => 
      contact.contact_id === contactId 
        ? { ...contact, is_favorite: !contact.is_favorite }
        : contact
    ));
  };

  const deleteContact = (contactId) => {
    setContacts(contacts.filter(contact => contact.contact_id !== contactId));
  };

  const addContact = (newContact) => {
    const contact = {
      contact_id: Date.now(),
      user_id: currentUser.id,
      is_favorite: false,
      photo: null,
      ...newContact
    };
    setContacts([...contacts, contact]);
    setShowAddModal(false);
  };

  const updateContact = (updatedContact) => {
    setContacts(contacts.map(contact => 
      contact.contact_id === updatedContact.contact_id ? updatedContact : contact
    ));
    setEditingItem(null);
  };

  const ContactCard = ({ contact }) => (
    <div className="bg-white rounded-xl shadow-md hover:shadow-lg transition-all duration-300 border border-gray-100 overflow-hidden group">
      <div className="p-6">
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center space-x-3">
            <div className="w-12 h-12 bg-gradient-to-r from-blue-500 to-purple-600 rounded-full flex items-center justify-center text-white font-semibold text-lg">
              {contact.name.charAt(0)}
            </div>
            <div>
              <h3 className="font-semibold text-gray-800 text-lg">{contact.name}</h3>
              <span className="text-sm text-gray-500 bg-gray-100 px-2 py-1 rounded-full">
                {getCategoryName(contact.category_id)}
              </span>
            </div>
          </div>
          <button 
            onClick={() => toggleFavorite(contact.contact_id)}
            className={`transition-colors duration-200 ${contact.is_favorite ? 'text-red-500' : 'text-gray-300 hover:text-red-500'}`}
          >
            <Heart fill={contact.is_favorite ? 'currentColor' : 'none'} size={20} />
          </button>
        </div>

        <div className="space-y-2 mb-4">
          <div className="flex items-center text-gray-600">
            <Phone size={16} className="mr-3 text-blue-500" />
            <span className="text-sm">{contact.phone}</span>
          </div>
          <div className="flex items-center text-gray-600">
            <Mail size={16} className="mr-3 text-green-500" />
            <span className="text-sm">{contact.email}</span>
          </div>
        </div>

        <div className="flex justify-end space-x-2 opacity-0 group-hover:opacity-100 transition-opacity duration-200">
          <button 
            onClick={() => setEditingItem(contact)}
            className="p-2 text-blue-600 hover:bg-blue-50 rounded-lg transition-colors"
          >
            <Edit size={16} />
          </button>
          <button 
            onClick={() => deleteContact(contact.contact_id)}
            className="p-2 text-red-600 hover:bg-red-50 rounded-lg transition-colors"
          >
            <Trash2 size={16} />
          </button>
        </div>
      </div>
    </div>
  );

  const AddEditModal = ({ isOpen, onClose, contact, onSave }) => {
    const [formData, setFormData] = useState({
      name: '',
      phone: '',
      email: '',
      category_id: categories[0]?.category_id || 1
    });

    useEffect(() => {
      if (contact) {
        setFormData(contact);
      } else {
        setFormData({
          name: '',
          phone: '',
          email: '',
          category_id: categories[0]?.category_id || 1
        });
      }
    }, [contact]);

    const handleSubmit = () => {
      if (formData.name && formData.phone && formData.email) {
        onSave(formData);
      }
    };

    if (!isOpen) return null;

    return (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
        <div className="bg-white rounded-xl max-w-md w-full p-6">
          <h2 className="text-xl font-bold mb-4">
            {contact ? 'Edit Contact' : 'Add New Contact'}
          </h2>
          <div className="space-y-4">
            <div>
              <div className="block text-sm font-medium text-gray-700 mb-1">Name</div>
              <input
                type="text"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>
            <div>
              <div className="block text-sm font-medium text-gray-700 mb-1">Phone</div>
              <input
                type="tel"
                value={formData.phone}
                onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>
            <div>
              <div className="block text-sm font-medium text-gray-700 mb-1">Email</div>
              <input
                type="email"
                value={formData.email}
                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>
            <div>
              <div className="block text-sm font-medium text-gray-700 mb-1">Category</div>
              <select
                value={formData.category_id}
                onChange={(e) => setFormData({ ...formData, category_id: parseInt(e.target.value) })}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                {categories.map(category => (
                  <option key={category.category_id} value={category.category_id}>
                    {category.name}
                  </option>
                ))}
              </select>
            </div>
            <div className="flex justify-end space-x-3 pt-4">
              <button
                type="button"
                onClick={onClose}
                className="px-4 py-2 text-gray-600 hover:text-gray-800 transition-colors"
              >
                Cancel
              </button>
              <button
                type="button"
                onClick={handleSubmit}
                className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
              >
                {contact ? 'Update' : 'Add'} Contact
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-purple-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center space-x-3">
              <div className="w-8 h-8 bg-gradient-to-r from-blue-600 to-purple-600 rounded-lg flex items-center justify-center">
                <Contact className="text-white" size={20} />
              </div>
              <h1 className="text-xl font-bold text-gray-900">Contact Manager</h1>
            </div>

            {currentUser && (
              <div className="flex items-center space-x-3">
                <div className="flex items-center space-x-2">
                  <div className="w-8 h-8 bg-gradient-to-r from-green-500 to-teal-600 rounded-full flex items-center justify-center text-white font-semibold text-sm">
                    {currentUser.name.charAt(0)}
                  </div>
                  <span className="text-sm font-medium text-gray-700">{currentUser.name}</span>
                </div>
              </div>
            )}
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Controls */}
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4 mb-8">
          <div className="flex flex-col sm:flex-row gap-4 flex-1">
            <div className="relative flex-1 max-w-md">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
              <input
                type="text"
                placeholder="Search contacts..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>

            <div className="relative">
              <Filter className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
              <select
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
                className="pl-10 pr-8 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 appearance-none bg-white"
              >
                <option value="all">All Categories</option>
                <option value="favorites">Favorites</option>
                {categories.map(category => (
                  <option key={category.category_id} value={category.category_id}>
                    {category.name}
                  </option>
                ))}
              </select>
            </div>
          </div>

          <button
            onClick={() => setShowAddModal(true)}
            className="flex items-center space-x-2 bg-gradient-to-r from-blue-600 to-purple-600 text-white px-4 py-2 rounded-lg hover:from-blue-700 hover:to-purple-700 transition-all duration-200 shadow-lg hover:shadow-xl"
          >
            <Plus size={20} />
            <span>Add Contact</span>
          </button>
        </div>

        {/* Stats */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <div className="bg-white rounded-xl shadow-md p-6 border border-gray-100">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Total Contacts</p>
                <p className="text-2xl font-bold text-gray-900">{filteredContacts.length}</p>
              </div>
              <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center">
                <Users className="text-blue-600" size={24} />
              </div>
            </div>
          </div>

          <div className="bg-white rounded-xl shadow-md p-6 border border-gray-100">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Favorites</p>
                <p className="text-2xl font-bold text-gray-900">
                  {contacts.filter(c => c.is_favorite && c.user_id === currentUser?.id).length}
                </p>
              </div>
              <div className="w-12 h-12 bg-red-100 rounded-full flex items-center justify-center">
                <Heart className="text-red-600" size={24} />
              </div>
            </div>
          </div>

          <div className="bg-white rounded-xl shadow-md p-6 border border-gray-100">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Categories</p>
                <p className="text-2xl font-bold text-gray-900">{categories.length}</p>
              </div>
              <div className="w-12 h-12 bg-green-100 rounded-full flex items-center justify-center">
                <Tag className="text-green-600" size={24} />
              </div>
            </div>
          </div>
        </div>

        {/* Contacts Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredContacts.map(contact => (
            <ContactCard key={contact.contact_id} contact={contact} />
          ))}
        </div>

        {filteredContacts.length === 0 && (
          <div className="text-center py-12">
            <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <Users className="text-gray-400" size={32} />
            </div>
            <h3 className="text-lg font-medium text-gray-900 mb-2">No contacts found</h3>
            <p className="text-gray-500">Try adjusting your search or filter criteria</p>
          </div>
        )}
      </div>

      {/* Add/Edit Modal */}
      <AddEditModal
        isOpen={showAddModal || editingItem !== null}
        onClose={() => {
          setShowAddModal(false);
          setEditingItem(null);
        }}
        contact={editingItem}
        onSave={editingItem ? updateContact : addContact}
      />
    </div>
  );
};

export default ContactManagementUI;
