import React, { useState, useEffect } from 'react';
import { User, Phone, Mail, Search, Plus, Edit2, Trash2, Users, BookOpen, Settings, Home, LogOut } from 'lucide-react';

const ContactBookApp = () => {
  const [currentView, setCurrentView] = useState('landing');
  const [currentUser, setCurrentUser] = useState(null);
  const [contacts, setContacts] = useState([]);
  const [categories, setCategories] = useState([
    { category_id: 1, name: 'Family' },
    { category_id: 2, name: 'Friends' },
    { category_id: 3, name: 'Work' },
    { category_id: 4, name: 'Business' }
  ]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [showAddContact, setShowAddContact] = useState(false);
  const [editingContact, setEditingContact] = useState(null);
  const [showAddCategory, setShowAddCategory] = useState(false);

  // Sample users for demo
  const [users] = useState([
    { user_id: 1, name: 'John Doe', email: '11@abc.', password: '123' },
    { user_id: 2, name: 'Jane Smith', email: 'jane@example.com', password: 'password123' }
  ]);

  const styles = {
    container: {
      minHeight: '100vh',
      fontFamily: 'system-ui, -apple-system, sans-serif',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    },

    // Landing Page Styles
    landingPage: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      minHeight: '100vh',
      padding: '2rem',
    },
    hero: {
      textAlign: 'center',
      maxWidth: '600px',
      color: 'white',
    },
    heroTitle: {
      fontSize: '3.5rem',
      fontWeight: 'bold',
      marginBottom: '1.5rem',
      textShadow: '2px 2px 4px rgba(0,0,0,0.3)',
    },
    heroSubtitle: {
      fontSize: '1.25rem',
      marginBottom: '2rem',
      opacity: '0.9',
    },
    ctaButton: {
      background: 'linear-gradient(45deg, #ff6b6b, #ee5a24)',
      color: 'white',
      border: 'none',
      padding: '1rem 2rem',
      fontSize: '1.1rem',
      borderRadius: '50px',
      cursor: 'pointer',
      boxShadow: '0 8px 25px rgba(0,0,0,0.2)',
      transition: 'all 0.3s ease',
      transform: 'translateY(0)',
    },
    ctaButtonHover: {
      transform: 'translateY(-2px)',
      boxShadow: '0 12px 35px rgba(0,0,0,0.3)',
    },

    // Auth Modal Styles
    modalOverlay: {
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0,0,0,0.7)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 1000,
    },
    modal: {
      backgroundColor: 'white',
      borderRadius: '20px',
      padding: '2rem',
      width: '90%',
      maxWidth: '400px',
      boxShadow: '0 20px 60px rgba(0,0,0,0.3)',
    },
    modalTitle: {
      fontSize: '1.5rem',
      fontWeight: 'bold',
      marginBottom: '1.5rem',
      textAlign: 'center',
      color: '#333',
    },
    inputGroup: {
      marginBottom: '1rem',
    },
    label: {
      display: 'block',
      marginBottom: '0.5rem',
      color: '#555',
      fontWeight: '500',
    },
    input: {
      width: '100%',
      padding: '0.75rem',
      border: '2px solid #e1e5e9',
      borderRadius: '10px',
      fontSize: '1rem',
      transition: 'border-color 0.3s ease',
      outline: 'none',
    },
    inputFocus: {
      borderColor: '#667eea',
    },
    button: {
      width: '100%',
      padding: '0.75rem',
      backgroundColor: '#667eea',
      color: 'white',
      border: 'none',
      borderRadius: '10px',
      fontSize: '1rem',
      cursor: 'pointer',
      transition: 'background-color 0.3s ease',
      marginBottom: '1rem',
    },
    buttonHover: {
      backgroundColor: '#5a67d8',
    },
    linkButton: {
      background: 'none',
      border: 'none',
      color: '#667eea',
      textDecoration: 'underline',
      cursor: 'pointer',
    },

    // Dashboard Styles
    dashboard: {
      display: 'flex',
      minHeight: '100vh',
    },
    sidebar: {
      width: '250px',
      backgroundColor: 'rgba(255,255,255,0.95)',
      backdropFilter: 'blur(10px)',
      padding: '2rem 1rem',
      borderRadius: '0 20px 20px 0',
      boxShadow: '5px 0 20px rgba(0,0,0,0.1)',
    },
    sidebarTitle: {
      fontSize: '1.5rem',
      fontWeight: 'bold',
      marginBottom: '2rem',
      color: '#333',
      textAlign: 'center',
    },
    navItem: {
      display: 'flex',
      alignItems: 'center',
      padding: '0.75rem 1rem',
      margin: '0.5rem 0',
      borderRadius: '10px',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      color: '#555',
    },
    navItemActive: {
      backgroundColor: '#667eea',
      color: 'white',
    },
    navItemHover: {
      backgroundColor: '#f0f0f0',
    },
    navIcon: {
      marginRight: '0.5rem',
    },

    // Main Content Styles
    mainContent: {
      flex: 1,
      padding: '2rem',
    },
    header: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      marginBottom: '2rem',
      color: 'white',
    },
    headerTitle: {
      fontSize: '2rem',
      fontWeight: 'bold',
    },
    userInfo: {
      display: 'flex',
      alignItems: 'center',
      gap: '1rem',
    },
    avatar: {
      width: '40px',
      height: '40px',
      backgroundColor: '#ff6b6b',
      borderRadius: '50%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      color: 'white',
      fontWeight: 'bold',
    },

    // Controls Styles
    controls: {
      display: 'flex',
      gap: '1rem',
      marginBottom: '2rem',
      flexWrap: 'wrap',
    },
    searchBox: {
      flex: 1,
      minWidth: '200px',
      position: 'relative',
    },
    searchInput: {
      width: '100%',
      padding: '0.75rem 1rem 0.75rem 3rem',
      border: '2px solid rgba(255,255,255,0.3)',
      borderRadius: '25px',
      fontSize: '1rem',
      backgroundColor: 'rgba(255,255,255,0.9)',
      outline: 'none',
    },
    searchIcon: {
      position: 'absolute',
      left: '1rem',
      top: '50%',
      transform: 'translateY(-50%)',
      color: '#666',
    },
    select: {
      padding: '0.75rem 1rem',
      border: '2px solid rgba(255,255,255,0.3)',
      borderRadius: '10px',
      backgroundColor: 'rgba(255,255,255,0.9)',
      fontSize: '1rem',
      outline: 'none',
    },
    addButton: {
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      padding: '0.75rem 1.5rem',
      backgroundColor: '#ff6b6b',
      color: 'white',
      border: 'none',
      borderRadius: '10px',
      cursor: 'pointer',
      fontSize: '1rem',
      transition: 'all 0.3s ease',
    },
    addButtonHover: {
      backgroundColor: '#ee5a24',
      transform: 'translateY(-2px)',
    },

    // Contact Grid Styles
    contactGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
      gap: '1.5rem',
    },
    contactCard: {
      backgroundColor: 'rgba(255,255,255,0.95)',
      borderRadius: '15px',
      padding: '1.5rem',
      boxShadow: '0 10px 30px rgba(0,0,0,0.1)',
      transition: 'all 0.3s ease',
      transform: 'translateY(0)',
    },
    contactCardHover: {
      transform: 'translateY(-5px)',
      boxShadow: '0 15px 40px rgba(0,0,0,0.2)',
    },
    contactHeader: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'flex-start',
      marginBottom: '1rem',
    },
    contactName: {
      fontSize: '1.25rem',
      fontWeight: 'bold',
      color: '#333',
      marginBottom: '0.5rem',
    },
    contactCategory: {
      fontSize: '0.875rem',
      color: '#667eea',
      backgroundColor: 'rgba(102, 126, 234, 0.1)',
      padding: '0.25rem 0.75rem',
      borderRadius: '20px',
      fontWeight: '500',
    },
    contactInfo: {
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      marginBottom: '0.5rem',
      color: '#666',
    },
    contactActions: {
      display: 'flex',
      gap: '0.5rem',
      marginTop: '1rem',
    },
    actionButton: {
      padding: '0.5rem',
      border: 'none',
      borderRadius: '8px',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    },
    editButton: {
      backgroundColor: '#feca57',
      color: 'white',
    },
    deleteButton: {
      backgroundColor: '#ff6b6b',
      color: 'white',
    },

    // Form Styles
    form: {
      display: 'grid',
      gap: '1rem',
    },
    formRow: {
      display: 'grid',
      gridTemplateColumns: '1fr 1fr',
      gap: '1rem',
    },
    formActions: {
      display: 'flex',
      gap: '1rem',
      justifyContent: 'flex-end',
      marginTop: '1rem',
    },
    cancelButton: {
      padding: '0.75rem 1.5rem',
      backgroundColor: '#95a5a6',
      color: 'white',
      border: 'none',
      borderRadius: '10px',
      cursor: 'pointer',
    },
    saveButton: {
      padding: '0.75rem 1.5rem',
      backgroundColor: '#27ae60',
      color: 'white',
      border: 'none',
      borderRadius: '10px',
      cursor: 'pointer',
    },

    // Empty State
    emptyState: {
      textAlign: 'center',
      color: 'rgba(255,255,255,0.8)',
      fontSize: '1.25rem',
      padding: '3rem',
    },
  };

  // Authentication Functions
  const handleLogin = (email, password) => {
    const user = users.find(u => u.email === email && u.password === password);
    if (user) {
      setCurrentUser(user);
      setCurrentView('dashboard');
      // Load sample contacts for demo
      loadSampleContacts(user.user_id);
    } else {
      alert('Invalid credentials');
    }
  };

  const loadSampleContacts = (userId) => {
    const sampleContacts = [
      {
        contact_id: 1,
        name: 'Alice Johnson',
        email: 'alice@example.com',
        phone: '+1 (555) 123-4567',
        category_id: 1,
        user_id: userId
      },
      {
        contact_id: 2,
        name: 'Bob Smith',
        email: 'bob@company.com',
        phone: '+1 (555) 234-5678',
        category_id: 3,
        user_id: userId
      },
      {
        contact_id: 3,
        name: 'Carol Wilson',
        email: 'carol@example.com',
        phone: '+1 (555) 345-6789',
        category_id: 2,
        user_id: userId
      }
    ];
    setContacts(sampleContacts);
  };

  const handleLogout = () => {
    setCurrentUser(null);
    setCurrentView('landing');
    setContacts([]);
  };

  // Contact Management Functions
  const addContact = (contactData) => {
    const newContact = {
      contact_id: Date.now(),
      ...contactData,
      user_id: currentUser.user_id
    };
    setContacts([...contacts, newContact]);
    setShowAddContact(false);
  };

  const updateContact = (contactData) => {
    setContacts(contacts.map(contact => 
      contact.contact_id === editingContact.contact_id 
        ? { ...contact, ...contactData }
        : contact
    ));
    setEditingContact(null);
  };

  const deleteContact = (contactId) => {
    if (window.confirm('Are you sure you want to delete this contact?')) {
      setContacts(contacts.filter(contact => contact.contact_id !== contactId));
    }
  };

  const addCategory = (categoryName) => {
    const newCategory = {
      category_id: Date.now(),
      name: categoryName
    };
    setCategories([...categories, newCategory]);
    setShowAddCategory(false);
  };

  // Filter contacts based on search and category
  const filteredContacts = contacts.filter(contact => {
    const matchesSearch = contact.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         contact.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         contact.phone.includes(searchTerm);
    const matchesCategory = selectedCategory === '' || contact.category_id.toString() === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  // Get category name by ID
  const getCategoryName = (categoryId) => {
    const category = categories.find(cat => cat.category_id === categoryId);
    return category ? category.name : 'Unknown';
  };

  // Landing Page Component
  const LandingPage = () => (
    <div style={styles.landingPage}>
      <div style={styles.hero}>
        <h1 style={styles.heroTitle}>Contact Book</h1>
        <p style={styles.heroSubtitle}>
          Organize your contacts beautifully. Connect with people that matter.
        </p>
        <button 
          style={styles.ctaButton}
          onMouseEnter={(e) => {
            e.target.style.transform = 'translateY(-2px)';
            e.target.style.boxShadow = '0 12px 35px rgba(0,0,0,0.3)';
          }}
          onMouseLeave={(e) => {
            e.target.style.transform = 'translateY(0)';
            e.target.style.boxShadow = '0 8px 25px rgba(0,0,0,0.2)';
          }}
          onClick={() => setCurrentView('login')}
        >
          Get Started
        </button>
      </div>
    </div>
  );

  // Auth Modal Component
  const AuthModal = ({ isLogin, onClose }) => {
    const [formData, setFormData] = useState({
      name: '',
      email: '',
      password: ''
    });

    const handleSubmit = (e) => {
      e.preventDefault();
      if (isLogin) {
        handleLogin(formData.email, formData.password);
      } else {
        // For demo, just redirect to login
        alert('Registration successful! Please login.');
        setCurrentView('login');
      }
    };

    return (
      <div style={styles.modalOverlay} onClick={onClose}>
        <div style={styles.modal} onClick={(e) => e.stopPropagation()}>
          <h2 style={styles.modalTitle}>
            {isLogin ? 'Welcome Back' : 'Create Account'}
          </h2>
          <form onSubmit={handleSubmit}>
            {!isLogin && (
              <div style={styles.inputGroup}>
                <label style={styles.label}>Name</label>
                <input
                  type="text"
                  style={styles.input}
                  value={formData.name}
                  onChange={(e) => setFormData({...formData, name: e.target.value})}
                  required
                />
              </div>
            )}
            <div style={styles.inputGroup}>
              <label style={styles.label}>Email</label>
              <input
                type="email"
                style={styles.input}
                value={formData.email}
                onChange={(e) => setFormData({...formData, email: e.target.value})}
                required
              />
            </div>
            <div style={styles.inputGroup}>
              <label style={styles.label}>Password</label>
              <input
                type="password"
                style={styles.input}
                value={formData.password}
                onChange={(e) => setFormData({...formData, password: e.target.value})}
                required
              />
            </div>
            <button type="submit" style={styles.button}>
              {isLogin ? 'Sign In' : 'Sign Up'}
            </button>
          </form>
          <div style={{ textAlign: 'center' }}>
            <button 
              style={styles.linkButton}
              onClick={() => setCurrentView(isLogin ? 'register' : 'login')}
            >
              {isLogin ? 'Need an account? Sign up' : 'Already have an account? Sign in'}
            </button>
          </div>
        </div>
      </div>
    );
  };

  // Contact Form Component
  const ContactForm = ({ contact, onSave, onCancel }) => {
    const [formData, setFormData] = useState({
      name: contact?.name || '',
      email: contact?.email || '',
      phone: contact?.phone || '',
      category_id: contact?.category_id || categories[0]?.category_id || 1
    });

    const handleSubmit = (e) => {
      e.preventDefault();
      onSave(formData);
    };

    return (
      <div style={styles.modalOverlay}>
        <div style={styles.modal}>
          <h2 style={styles.modalTitle}>
            {contact ? 'Edit Contact' : 'Add New Contact'}
          </h2>
          <form onSubmit={handleSubmit} style={styles.form}>
            <div style={styles.inputGroup}>
              <label style={styles.label}>Name</label>
              <input
                type="text"
                style={styles.input}
                value={formData.name}
                onChange={(e) => setFormData({...formData, name: e.target.value})}
                required
              />
            </div>
            <div style={styles.formRow}>
              <div style={styles.inputGroup}>
                <label style={styles.label}>Email</label>
                <input
                  type="email"
                  style={styles.input}
                  value={formData.email}
                  onChange={(e) => setFormData({...formData, email: e.target.value})}
                  required
                />
              </div>
              <div style={styles.inputGroup}>
                <label style={styles.label}>Phone</label>
                <input
                  type="tel"
                  style={styles.input}
                  value={formData.phone}
                  onChange={(e) => setFormData({...formData, phone: e.target.value})}
                  required
                />
              </div>
            </div>
            <div style={styles.inputGroup}>
              <label style={styles.label}>Category</label>
              <select
                style={styles.input}
                value={formData.category_id}
                onChange={(e) => setFormData({...formData, category_id: parseInt(e.target.value)})}
              >
                {categories.map(category => (
                  <option key={category.category_id} value={category.category_id}>
                    {category.name}
                  </option>
                ))}
              </select>
            </div>
            <div style={styles.formActions}>
              <button type="button" style={styles.cancelButton} onClick={onCancel}>
                Cancel
              </button>
              <button type="submit" style={styles.saveButton}>
                Save
              </button>
            </div>
          </form>
        </div>
      </div>
    );
  };

  // Category Form Component
  const CategoryForm = ({ onSave, onCancel }) => {
    const [categoryName, setCategoryName] = useState('');

    const handleSubmit = (e) => {
      e.preventDefault();
      if (categoryName.trim()) {
        onSave(categoryName.trim());
      }
    };

    return (
      <div style={styles.modalOverlay}>
        <div style={styles.modal}>
          <h2 style={styles.modalTitle}>Add New Category</h2>
          <form onSubmit={handleSubmit}>
            <div style={styles.inputGroup}>
              <label style={styles.label}>Category Name</label>
              <input
                type="text"
                style={styles.input}
                value={categoryName}
                onChange={(e) => setCategoryName(e.target.value)}
                required
              />
            </div>
            <div style={styles.formActions}>
              <button type="button" style={styles.cancelButton} onClick={onCancel}>
                Cancel
              </button>
              <button type="submit" style={styles.saveButton}>
                Add Category
              </button>
            </div>
          </form>
        </div>
      </div>
    );
  };

  // Dashboard Component
  const Dashboard = () => {
    const [activeTab, setActiveTab] = useState('contacts');

    const sidebarItems = [
      { id: 'contacts', label: 'Contacts', icon: Users },
      { id: 'categories', label: 'Categories', icon: BookOpen },
      { id: 'settings', label: 'Settings', icon: Settings },
    ];

    return (
      <div style={styles.dashboard}>
        <div style={styles.sidebar}>
          <h2 style={styles.sidebarTitle}>Contact Book</h2>
          <nav>
            {sidebarItems.map(item => {
              const Icon = item.icon;
              return (
                <div
                  key={item.id}
                  style={{
                    ...styles.navItem,
                    ...(activeTab === item.id ? styles.navItemActive : {})
                  }}
                  onClick={() => setActiveTab(item.id)}
                >
                  <Icon size={20} style={styles.navIcon} />
                  {item.label}
                </div>
              );
            })}
            <div
              style={{...styles.navItem, marginTop: '2rem'}}
              onClick={handleLogout}
            >
              <LogOut size={20} style={styles.navIcon} />
              Logout
            </div>
          </nav>
        </div>

        <div style={styles.mainContent}>
          <div style={styles.header}>
            <h1 style={styles.headerTitle}>
              {activeTab === 'contacts' ? 'My Contacts' : 
               activeTab === 'categories' ? 'Categories' : 'Settings'}
            </h1>
            <div style={styles.userInfo}>
              <div style={styles.avatar}>
                {currentUser?.name?.charAt(0).toUpperCase()}
              </div>
              <span>{currentUser?.name}</span>
            </div>
          </div>

          {activeTab === 'contacts' && (
            <>
              <div style={styles.controls}>
                <div style={styles.searchBox}>
                  <Search size={20} style={styles.searchIcon} />
                  <input
                    type="text"
                    placeholder="Search contacts..."
                    style={styles.searchInput}
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                  />
                </div>
                <select
                  style={styles.select}
                  value={selectedCategory}
                  onChange={(e) => setSelectedCategory(e.target.value)}
                >
                  <option value="">All Categories</option>
                  {categories.map(category => (
                    <option key={category.category_id} value={category.category_id}>
                      {category.name}
                    </option>
                  ))}
                </select>
                <button
                  style={styles.addButton}
                  onClick={() => setShowAddContact(true)}
                >
                  <Plus size={20} />
                  Add Contact
                </button>
              </div>

              {filteredContacts.length === 0 ? (
                <div style={styles.emptyState}>
                  {contacts.length === 0 ? 
                    'No contacts yet. Add your first contact!' : 
                    'No contacts match your search criteria.'}
                </div>
              ) : (
                <div style={styles.contactGrid}>
                  {filteredContacts.map(contact => (
                    <div
                      key={contact.contact_id}
                      style={styles.contactCard}
                      onMouseEnter={(e) => {
                        e.currentTarget.style.transform = 'translateY(-5px)';
                        e.currentTarget.style.boxShadow = '0 15px 40px rgba(0,0,0,0.2)';
                      }}
                      onMouseLeave={(e) => {
                        e.currentTarget.style.transform = 'translateY(0)';
                        e.currentTarget.style.boxShadow = '0 10px 30px rgba(0,0,0,0.1)';
                      }}
                    >
                      <div style={styles.contactHeader}>
                        <div>
                          <h3 style={styles.contactName}>{contact.name}</h3>
                          <span style={styles.contactCategory}>
                            {getCategoryName(contact.category_id)}
                          </span>
                        </div>
                      </div>
                      <div style={styles.contactInfo}>
                        <Mail size={16} />
                        <span>{contact.email}</span>
                      </div>
                      <div style={styles.contactInfo}>
                        <Phone size={16} />
                        <span>{contact.phone}</span>
                      </div>
                      <div style={styles.contactActions}>
                        <button
                          style={{...styles.actionButton, ...styles.editButton}}
                          onClick={() => setEditingContact(contact)}
                        >
                          <Edit2 size={16} />
                        </button>
                        <button
                          style={{...styles.actionButton, ...styles.deleteButton}}
                          onClick={() => deleteContact(contact.contact_id)}
                        >
                          <Trash2 size={16} />
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </>
          )}

          {activeTab === 'categories' && (
            <>
              <div style={styles.controls}>
                <button
                  style={styles.addButton}
                  onClick={() => setShowAddCategory(true)}
                >
                  <Plus size={20} />
                  Add Category
                </button>
              </div>
              <div style={styles.contactGrid}>
                {categories.map(category => (
                  <div key={category.category_id} style={styles.contactCard}>
                    <h3 style={styles.contactName}>{category.name}</h3>
                    <p style={{ color: '#666' }}>
                      {contacts.filter(c => c.category_id === category.category_id).length} contacts
                    </p>
                  </div>
                ))}
              </div>
            </>
          )}

          {activeTab === 'settings' && (
            <div style={styles.contactCard}>
              <h3 style={styles.contactName}>Account Settings</h3>
              <div style={styles.contactInfo}>
                <User size={16} />
                <span>{currentUser?.name}</span>
              </div>
              <div style={styles.contactInfo}>
                <Mail size={16} />
                <span>{currentUser?.email}</span>
              </div>
            </div>
          )}
        </div>
      </div>
    );
  };

  return (
    <div style={styles.container}>
      {currentView === 'landing' && <LandingPage />}
      {currentView === 'login' && (
        <AuthModal isLogin={true} onClose={() => setCurrentView('landing')} />
      )}
      {currentView === 'register' && (
        <AuthModal isLogin={false} onClose={() => setCurrentView('landing')} />
      )}
      {currentView === 'dashboard' && <Dashboard />}

      {showAddContact && (
        <ContactForm
          onSave={addContact}
          onCancel={() => setShowAddContact(false)}
        />
      )}

      {editingContact && (
        <ContactForm
          contact={editingContact}
          onSave={updateContact}
          onCancel={() => setEditingContact(null)}
        />
      )}

      {showAddCategory && (
        <CategoryForm
          onSave={addCategory}
          onCancel={() => setShowAddCategory(false)}
        />
      )}
    </div>
  );
};

export default ContactBookApp;
